INSERT INTO `wp_posts` VALUES (9,1,'2020-07-08 20:32:34','2020-07-08 18:32:34','<!-- wp:heading {\"align\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Unser Projekt      &nbsp;</h2>\n<!-- /wp:heading -->\n\n<!-- wp:tadv/classic-paragraph -->\n<p style=\"text-align: center;\"><span style=\"color: #101820;\">Wir grnden ein Cloud-Storage-Unternehmen namens ROS, ein Akronym fr <strong>R</strong>eliable <strong>O</strong>bject <strong>S</strong>torage, dass sich auf CloudStorage fixiert. Ziel ist es Daten sowohl per Smartphone, sowie Desktop/Laptop jederzeit hochladen und zu einem spteren Zeitpunkt wieder abrufen zu knnen.</span></p>\n<!-- /wp:tadv/classic-paragraph -->\n\n<!-- wp:spacer {\"height\":70} -->\n<div style=\"height:70px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:nextend/smartslider3 {\"slider\":\"2\"} -->\n<div class=\"wp-block-nextend-smartslider3\">[smartslider3 slider=\"2\"]</div>\n<!-- /wp:nextend/smartslider3 -->\n\n<!-- wp:spacer {\"height\":70} -->\n<div style=\"height:70px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading -->\n<h2><font style=\"vertical-align: inherit;\"><font style=\"vertical-align: inherit;\">Branding Book</font></font> </h2>\n<!-- /wp:heading -->\n\n<!-- wp:tadv/classic-paragraph -->\n<p><br /><span style=\"color: #101820;\">Ein Branding Book oder auch Corporate Design Book genannt ist eine interne Richtlinien Gestaltung fr Grafiken und Texte. Dabei sollen Details, wie die der Typografie genau beschrieben und erlutert werden.</span></p>\n<p><span style=\"color: #101820;\">Weitere Aspekte, die detailliert ausgeschrieben werden sollen, sind unteranderem die des Logos, welches ein groer Bestandteil all unserer Produkte ist und berall zu finden <a href=\"localhost/ros/team\" style=\"color: #101820;\">ist.</a></span></p>\n<!-- /wp:tadv/classic-paragraph -->\n\n<!-- wp:image {\"align\":\"left\",\"id\":233,\"width\":350,\"height\":322,\"sizeSlug\":\"large\"} -->\n<div class=\"wp-block-image\"><figure class=\"alignleft size-large is-resized\"><img src=\"http://localhost/ros/wp-content/uploads/2020/09/BB-Cover-1024x944.png\" alt=\"\" class=\"wp-image-233\" width=\"350\" height=\"322\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:file {\"id\":198,\"href\":\"http://localhost/ros/wp-content/uploads/2020/09/BrandingBook_ros.pdf\"} -->\n<div class=\"wp-block-file\"><a href=\"http://localhost/ros/wp-content/uploads/2020/09/BrandingBook_ros.pdf\"><br><span class=\"has-inline-color has-accent-color\">BrandingBook_ros</span>.pdf</a><a href=\"http://localhost/ros/wp-content/uploads/2020/09/BrandingBook_ros.pdf\" class=\"wp-block-file__button\" download><span class=\"has-inline-color has-accent-color\"><strong>Herunterladen</strong></span></a></div>\n<!-- /wp:file -->\n\n<!-- wp:spacer {\"height\":38} -->\n<div style=\"height:38px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:separator {\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator is-style-wide\"/>\n<!-- /wp:separator -->\n\n<!-- wp:spacer {\"height\":32} -->\n<div style=\"height:32px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:html -->\n<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2659.864635056881!2d16.399559657194615!3d48.18995956009425!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x476d075e036ac393%3A0x9326dcbe2d63c038!2sHTL%20Rennweg!5e0!3m2!1sde!2sat!4v1598544117048!5m2!1sde!2sat\" width=\"1500\" height=\"300\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\" aria-hidden=\"false\" tabindex=\"0\"></iframe>\n<!-- /wp:html -->\n\n<!-- wp:spacer {\"height\":39} -->\n<div style=\"height:39px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:paragraph -->\n<p><a href=\"http://localhost/ros/team/\">Zu Das Team</a></p>\n<!-- /wp:paragraph -->','ROS-Cloud','','publish','closed','c