INSERT INTO `wp_posts` VALUES (9,1,'2020-07-08 20:32:34','2020-07-08 18:32:34','<!-- wp:heading {\"align\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Unser Projekt      &nbsp;</h2>\n<!-- /wp:heading -->\n\n<!-- wp:tadv/classic-paragraph -->\n<p style=\"text-align: center;\"><span style=\"color: #101820;\">Wir gründen ein „Cloud-Storage-Unternehmen“ namens ROS, ein Akronym für <strong>R</strong>eliable <strong>O</strong>bject <strong>S</strong>torage, dass sich auf Cloud Storage fixiert. Ziel ist es Daten sowohl per Smartphone, sowie Desktop/Laptop jederzeit hochladen und zu einem späteren Zeitpunkt wieder abrufen zu können.</span></p>\n<!-- /wp:tadv/classic-paragraph -->\n\n<!-- wp:spacer {\"height\":70} -->\n<div style=\"height:70px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:nextend/smartslider3 {\"slider\":\"2\"} -->\n<div class=\"wp-block-nextend-smartslider3\">[smartslider3 slider=\"2\"]</div>\n<!-- /wp:nextend/smartslider3 -->\n\n<!-- wp:spacer {\"height\":70} -->\n<div style=\"height:70px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading -->\n<h2><font style=\"vertical-align: inherit;\"><font style=\"vertical-align: inherit;\">Branding Book</font></font> </h2>\n<!-- /wp:heading -->\n\n<!-- wp:tadv/classic-paragraph -->\n<p><br /><span style=\"color: #101820;\">Ein Branding Book oder auch Corporate Design Book genannt ist eine interne Richtlinien Gestaltung für Grafiken und Texte. Dabei sollen Details, wie die der Typografie genau beschrieben und erläutert werden. </span></p>\n<p><span style=\"color: #101820;\">Weitere Aspekte, die detailliert ausgeschrieben werden sollen, sind unteranderem die des Logos, welches ein großer Bestandteil all unserer Produkte ist und überall zu finden <a href=\"localhost/ros/team\" style=\"color: #101820;\">ist.</a></span></p>\n<!-- /wp:tadv/classic-paragraph -->\n\n<!-- wp:image {\"align\":\"left\",\"id\":233,\"width\":350,\"heigh